const url = $input.first().json.url;

// Extract the LinkedIn user ID from the URL
const userId = url.split('/in/')[1].replace(/\/$/, '');

return { userId };

var url = $input.first().json.body.url // your full URL

var queryEncoded = url.split("query=")[1].split("&")[0];
var decoded = decodeURIComponent(queryEncoded);

var rolesSection = decoded.match(/type:CURRENT_TITLE.*?values:List\((.*?)\)\)/)[1];

var regex = /text:([^\),]+)/g;
var roles = [];
var m;
while ((m = regex.exec(rolesSection)) !== null) {
  roles.push(decodeURIComponent(m[1].replace(/\+/g, ' ')));
}

return {roles}

function extractPersonData(jsonData) {
  const extractedData = [];

  jsonData.forEach(person => {
    const personName = `${person.first_name} ${person.last_name}`;
    const linkedinUrl = person.public_profile_url;

    if (person.current_positions && person.current_positions.length > 0) {
      person.current_positions.forEach(position => {
        const companyName = position.company;
        const companyId = position.company_id;
        const personRole = position.role;

        extractedData.push({
          personName: personName,
          linkedinUrl: linkedinUrl,
          companyName: companyName,
          personRole: personRole,
          companyId: companyId,
        });
      });
    }
  });

  return extractedData;
}

