const url = $input.first().json.website;

let domain = '';
try {
  if (!url || typeof url !== 'string') {
    throw new Error('Empty or non-string URL');
  }

  // Add protocol if missing
  const normalizedUrl = url.startsWith('http') ? url : 'https://' + url;

  // Use regex to extract domain
  const match = normalizedUrl.match(/^https?:\/\/([^\/?#]+)(?:[\/?#]|$)/i);
  if (match) {
    domain = match[1].replace(/^www\./, '');
  } else {
    throw new Error('Regex failed to match');
  }
} catch (e) {
  domain = 'Invalid URL';
}
const new_url = "https://" + domain
return {
  domain,
  "url": new_url
};

